-- ----------------------------
-- Table structure for tb_dish
-- ----------------------------
DROP TABLE IF EXISTS `tb_dish`;
CREATE TABLE `tb_dish`  (
  `dish_id` varchar(20) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
  `dish_name_thai` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'ชื่อภาษาไทย ',
  `dish_detail` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'รายละเอียดภาษาไทย \r\nอธิบายจุดเด่น / จุดขาย ของอาหาร ไฮไลท์ ลักษณะเด่นทางกายภาพ เช่น เนื้อสัมผัส กลิ่น รสชาติ รูปลักษณ์ เพื่อสร้างความน่าสนใจของนักท่องเที่ยว ประมาณ 3-4 บรรทัด ',
  `dish_story` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'เรื่องเล่าของจานอาหาร ภาษาไทย \r\nอธิบายเรื่องราวที่เชื่อมโยงกับสังคม ชุมชน ความเชื่อ หรืออื่นๆที่เกี่ยวข้อง แบบย่อ',
  `dish_made` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'วิธีการปรุงอาหารภาษาไทย อธิบายวัตถุดิบ ปริมาณ สัดส่วน และขั้นตอนการปรุงอาหาร \r\n(ระบุเป็นข้อๆ) เพื่อนำมาเป็นข้อมูลวิเคราะห์คุณค่าทางโภชนาการ',
  `dish_eat` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'เทคนิคการทานอาหาร ภาษาไทย อธิบายเคล็ดลับ เทคนิค วิธีการทานอาหารที่น่าสนใจเพื่อสร้างประสบการณ์ที่น่าจดจำของนักท่องเที่ยว',
  `dish_season` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'วันเวลาเริ่มต้น-สิ้นสุด ฤดูกาล ช่วงเวลาที่เหมาะสมในการปรุงอาหาร  ระบุเป็นเดือน หรือ ตลอดปี ',
  `dish_price` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'ราคาโดยประมาณ ระบุราคาเป็นช่วงหรือเป็นจาน ตามความเหมาะสม\r\n',
  `dish_made_type` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'รูปแบบการประกอบอาหาร ระบุสั้นๆ ถึงวิธีการทำ เช่น ต้ม ปิ้ง ย่าง ',
  `dish_food_type` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT 'ประเภทอาหาร \nเลือกประเภทใด ประเภทหนึ่งต่อไปนี้... ของคาว / ของหวาน / อาหารว่าง / เครื่องดื่ม\r\n',
  `dish_region` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dish_food_info` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dish_health_info` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dish_ingredients` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dish_restaurants` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dish_community` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  `dis_ref` text CHARACTER SET utf8 COLLATE utf8_bin NULL,
  PRIMARY KEY (`dish_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_bin ROW_FORMAT = Dynamic;

