INSERT INTO `tb_restaurant` VALUES ('Z050027', 'ร้านศิรดา', 'Sirada Ko Yo', 'บริการอาหารและเครื่องดื่ม สไตล์อาหารไทย อาหารปักษ์ใต้แท้ รสชาติเข้มข้นถึงใจ บรรยากาศสบายๆ ริมทะเลสาบสงขลา อาหารทะเลสดๆ จากทะเลสาบสงขลา อาหารมีความสด สะอาด อร่อย ของวัตถุดิบ ราคาไม่แพง มีทั้งกุ้ง หอย ปู หมึก นอกจากนี้ยังสามารถมองออกไปยังทะเลสาบสงขลา', 'เปิดบริการทุกวัน 11.00 - 22.00 น.', 'https://www.facebook.com/sirada.co/', '074-591-595', '7.172423', '100.546201', '8/2 หมู่ที่ 2 ตำบลเกาะยอ, Songkhla, Thailand, Songkhla', 'ใต้', 'สงขลา ', '', '', 'Wong nai. (2564).ศิรดา.  สืบค้น 9 สิงหาคม 2564, จาก https://www.wongnai.com/restaurants/7569O');
