INSERT INTO `tb_restaurant` VALUES ('Z060016', '-', 'Seed to Feed Restaurant & Farm Products', 'ร้านออร์แกนิค Seed to Feed เกาะพะงัน เหมาะสำหรับสายคนรักสุขภาพที่เดินทางไปเกาะพะงัน ร้านนี้เป็นร้านที่ต้องแแนะนำให้นักท่องเที่ยวไปลองชิม ลองสัมผัสกับอาหารที่ปลอดสารพิษ โดยร้านอาหารนี้ใช้วัตถุดิบที่ปลูกเองในฟาร์มหลังร้าน และไม่ใช้สารเคมีในการประกอบอาหาร', 'เปิดทุกวัน เวลา 07.30 - 22.00 น.', 'Page : Seed to Feed Restaurant & Farm Products', '091 803 1000', '10.10583062', '100.0502718', '53 ม.5 เกาะพะงัน อำเภอเกาะพะงัน จังหวัดสุราษฎร์ธานี', 'ใต้', 'สุราษฎร์ธานี', 'd-Z060023', 'a-Z060005', '');
